Template.Design:
@inherits Umbraco.Web.Mvc.UmbracoTemplatePage<ContentModels.HomePage>
@using ContentModels = Umbraco.Web.PublishedContentModels;
@{
	Layout = "SiteLayout.cshtml";
}

@section Head {
	<script type="text/javascript">
		alert("Umbraco Rocks");
	</script>
}

<h1>@Umbraco.Field("pageName")</h1>
@Umbraco.Field("bodyText")
@CurrentPage.GetGridHtml("frontpageArticle")

@inherits Umbraco.Web.Mvc.UmbracoTemplatePage<ContentModels.NewsArea>
@using ContentModels = Umbraco.Web.PublishedContentModels;
@{
	Layout = "SiteLayout.cshtml";
}


@{
	var selection = CurrentPage.Site().FirstChild("newsArea").Children("newsItem").Where("Visible");
}
<ul>
	@foreach(var item in selection){
		<li>
			<a href="@item.Url">@item.Name</a>
		</li>
	}
</ul>

@inherits Umbraco.Web.Mvc.UmbracoTemplatePage
@{
	Layout = null;
}
<!DOCTYPE html>
<html lang="en">
	<head>
		<meta charset="utf-8">
		<title>@Umbraco.Field("pageName")</title>

		<link rel="stylesheet" type="text/css" href="@Url.Content("~/css/style.css")" />
		<link rel="stylesheet" type="text/css" href="@Url.Content("~/css/bootstrap.min.css")" />
		<script type="text/javascript" src="@Url.Content("~/scripts/test.js")"></script>
		@RenderSection("Head", required:false)
	</head>
	<body>
		@RenderBody()
		@Umbraco.Field("footer", recursive: true)
	</body>
</html>

@inherits Umbraco.Web.Mvc.UmbracoTemplatePage
@{
	Layout = null;
}

